mapper.update:
update employees set first_name = #{first_name}, last_name = #{last_name}, email = #{email}, phone_number = #{phone_number}, hire_date = #{hire_date}, job_id = #{job_id}, salary = #{salary}, commission_pct = #{commission_pct}, manager_id = null, manager_id = #{manager_id},
department_id = null department_id = #{department_id}
where employee_id = #{employee_id}

mapper.insert:
insert into employees values (#{employee_id},#{first_name}, #{last_name},#{email},#{phone_number},#{hire_date},#{job_id},#{salary}, #{commission_pct}, null, #{department_id},
null #{manager_id}
)

mapper.select:

select * from employees
where 1=1


	and department_id = #{deptid}



	and job_id = #{jobid}



	and hire_date >= #{hdate}



	and salary >= #{salary}

order by 1
